import React, { useState } from 'react';

function Header() {
  const [currentPage, setCurrentPage] = useState('Kinder-1'); // Set the initial current page

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <header>
      <ul className="nav nav-tabs">
        {/* Kinder Tabs */}
        <li className="nav-item">
          <a
            href="#Kinder-1"
            onClick={() => handlePageChange('Kinder-1')}
            className={currentPage === 'Kinder-1' ? 'nav-link active' : 'nav-link'}
          >
            Kinder 1
          </a>
        </li>
        <li className="nav-item">
          <a
            href="#Kinder-2"
            onClick={() => handlePageChange('Kinder-2')}
            className={currentPage === 'Kinder-2' ? 'nav-link active' : 'nav-link'}
          >
            Kinder 2
          </a>
        </li>
        <li className="nav-item">
          <a
            href="#Kinder-3"
            onClick={() => handlePageChange('Kinder-3')}
            className={currentPage === 'Kinder-3' ? 'nav-link active' : 'nav-link'}
          >
            Kinder 3
          </a>
        </li>

        {/* 1st Grade Tabs */}
        <li className="nav-item">
          <a
            href="#1st-1"
            onClick={() => handlePageChange('1st-1')}
            className={currentPage === '1st-1' ? 'nav-link active' : 'nav-link'}
          >
            1st Grade 1
          </a>
        </li>
        <li className="nav-item">
          <a
            href="#1st-2"
            onClick={() => handlePageChange('1st-2')}
            className={currentPage === '1st-2' ? 'nav-link active' : 'nav-link'}
          >
            1st Grade 2
          </a>
        </li>
        <li className="nav-item">
          <a
            href="#1st-3"
            onClick={() => handlePageChange('1st-3')}
            className={currentPage === '1st-3' ? 'nav-link active' : 'nav-link'}
          >
            1st Grade 3
          </a>
        </li>
      </ul>
    </header>
  );
}

export default Header;
