import React from 'react';

const gradesData = [
  {
    grade: 'Kindergarten',
    classes: [
      { id: 1, name: 'Class A', resources: '/Kindergarten/ClassA-resources' },
      { id: 2, name: 'Class B', resources: '/Kindergarten/ClassB-resources' },
      { id: 3, name: 'Class C', resources: '/Kindergarten/ClassC-resources' },
    ],
  },
  {
    grade: 'Grade 1',
    classes: [
      { id: 1, name: 'Class A', resources: '/Grade1/ClassA-resources' },
      { id: 2, name: 'Class B', resources: '/Grade1/ClassB-resources' },
      { id: 3, name: 'Class C', resources: '/Grade1/ClassC-resources' },
    ],
  },
  {
    grade: 'Grade 2',
    classes: [
      { id: 1, name: 'Class A', resources: '/Grade2/ClassA-resources' },
      { id: 2, name: 'Class B', resources: '/Grade2/ClassB-resources' },
      { id: 3, name: 'Class C', resources: '/Grade2/ClassC-resources' },
    ],
  },
  // Add more grades and classes as needed
];

const TeacherPages = () => {
  return (
    <section>
      <h2>Teacher Pages</h2>
      {gradesData.map((gradeData) => (
        <div key={gradeData.grade}>
          <h3>{gradeData.grade}</h3>
          <ul>
            {gradeData.classes.map((teacherClass) => (
              <li key={teacherClass.id}>
                <a href={teacherClass.resources}>{teacherClass.name}</a>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </section>
  );
};

export default TeacherPages;
