export default function Footer() {
  // Function to handle the "Back to Top" button click event
  const handleBackToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // Return the JSX representing the footer section
  return (
    <footer className="row">
      <div className="col-sm-12 text-center">
        {/* Footer Image */}
        <img
          src={footerImage}
          alt="Footer Image"
          style={{ maxWidth: "25%" }}
        />
        {/* GitHub link */}
        <span>
          GitHub: <a href="https://github.com/kpetrosky">https://github.com/kpetrosky</a>
        </span>
        {/* LinkedIn link */}
        <span>
          LinkedIn: <a href="https://linkedin.com/in/krystal-petrosky-ab061126a">linkedin.com/in/krystal-petrosky-ab061126a</a>
        </span>
        {/* Copyright */}
        <span>
          Â© 2023
        </span>
        {/* "Back to Top" button */}
        <div className="back-to-top" onClick={handleBackToTop}>
          Back to Top
        </div>
      </div>
    </footer>
  );
}
