import React from 'react';

const WeeklySchedule = ({ curriculumPlanner, onUpdate }) => {
  return (
    <div>
      <h2>Weekly Curriculum Planner</h2>
      <table>
        <thead>
          <tr>
            <th>Day</th>
            <th>Task</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(curriculumPlanner).map(day => (
            <tr key={day}>
              <td>{day}</td>
              <td>
                <input
                  type="text"
                  value={curriculumPlanner[day]}
                  onChange={event => onUpdate(day, event.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WeeklySchedule;
