// export default Resources;

import React from 'react';

const Resources = () => {
  // Define an array of resources with their names, image URLs, and alt text

    const resourcesList = [
        {
          name: 'Resource 1: iReady',
          image: '../../images/iready.png',
          alt: 'iReady',
          link: 'https://readyclassroomcentral.com/'
        },
    {
      name: 'Resource 2: Typingclub',
      image: 'url-to-image-2.jpg',
      alt: 'Typingclub',
      link: 'https://www.typingclub.com/'
    },
    {
      name: 'Resource 3: Dragon Drop',
      image: 'url-to-image-3.jpg',
      alt: 'Dragon Drop',
      link: 'https://www.roomrecess.com/games/DragonDrop/play.html'
    },
    {
      name: 'Resource 4: HP computer skills games',
      image: 'url-to-image-4.jpg',
      alt: 'HP computer skills games',
      link: 'https://www.hp.com/us-en/shop/tech-takes/mouse-and-keyboarding-games'
    }
    // Add more resources as needed
  ];

  return (
    <section>
      <h2>Resources</h2>
      <ul className="resources-list">
        {resourcesList.map((resource, index) => (
          <li key={index} className="resource-item">
            <img src={resource.image} alt={resource.alt} />
            <a
              href={resource.link}
              target="_blank"
              rel="noopener noreferrer"
              className="resource-link"
            >
              {resource.name}
            </a>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default Resources;
