import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import Announcements from '../src/components/Announcements';
import Assignments from '../src/components/Assignments';
import Resources from '../src/components/Resources';
import Footer from '../src/components/Footer';
import title from '../../src/components/images/title image.png';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 'Kinder',
    };
  }

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { currentPage } = this.state;

    return (
      <div className="App">
        <Header currentPage={currentPage} handlePageChange={this.handlePageChange} />
        <main>
          {currentPage === 'Kinder' && (
            <div>
              <Announcements />
              {/* Add Kinder Teachers components here */}
            </div>
          )}
          {currentPage === '1st' && (
            <div>
              <Assignments />
              {/* Add 1st Grade Teachers components here */}
            </div>
          )}
          {currentPage === '2nd' && (
            <div>
              <Resources />
              {/* Add 2nd Grade Teachers components here */}
            </div>
          )}
          {/* Add more components for other grade levels */}
        </main>
        <Footer />
      </div>
    );
  }
}

export default App;
