import React, { Component } from 'react';
import Header from './components/pages/Header';
import Announcements from './components/pages/Announcements';
import Assignments from './components/pages/Assignments';
import Resources from './components/pages/Resources';
import TeacherPages from './components/pages/TeacherPages';
import CurriculumPlanner from './components/pages/CurriculumPlanner'; // Import the CurriculumPlanner component

import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      currentPage: 'announcements',
      curriculumPlanner: {
        Monday: '',
        Tuesday: '',
        Wednesday: '',
        Thursday: '',
        Friday: '',
        Saturday: '',
        Sunday: '',
      },
    };
  }

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  handleUpdate = (day, task) => {
    this.setState(prevState => ({
      curriculumPlanner: {
        ...prevState.curriculumPlanner,
        [day]: task,
      },
    }));
  };

  render() {
    const { currentPage, curriculumPlanner } = this.state;

    return (
      <div className="App">
        <Header currentPage={currentPage} handlePageChange={this.handlePageChange} />
        <main className="App-content">
          {currentPage === 'announcements' && <Announcements />}
          {currentPage === 'assignments' && <Assignments />}
          {currentPage === 'resources' && <Resources />}
          {currentPage === 'teacherPages' && <TeacherPages />}
          {currentPage === 'CurriculumPlanner' && (
            <CurriculumPlanner curriculumPlanner={curriculumPlanner} onUpdate={this.handleUpdate} />
          )} {/* Render the CurriculumPlanner component */}
        </main>
        <footer>{/* Footer content goes here */}</footer>
      </div>
    );
  }
}

export default App;
